import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const CLIENT_ID = "ad1d9256da1648fe842417e4533e59e8";
  const REDIRECT_URI = "http://localhost:3000";
  const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
  const RESPONSE_TYPE = "token";
  const SCOPE = "user-follow-read";

  const [token, setToken] = useState("");
  const [tracks, setTracks] = useState([]);
  const [playlist, setPlaylist] = useState("");

  useEffect(() => {
    const hash = window.location.hash;
    let token = window.localStorage.getItem("token");

    if (!token && hash) {
      token = hash
        .substring(1)
        .split("&")
        .find((elem) => elem.startsWith("access_token"))
        .split("=")[1];

      window.location.hash = "";
      window.localStorage.setItem("token", token);
    }
    setToken(token);
  }, []);

  const logout = () => {
    setToken("");
    setTracks([]);
    setPlaylist("");
    window.localStorage.removeItem("token");
  };

  const getPlaylistTracks = async () => {
    const { data } = await axios.get(
      "https://api.spotify.com/v1/playlists/2NzyWV9sT9rnKMncFHCkJG",
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: {
          uris: [],
        },
      }
    );

    setPlaylist(data.name);
    setTracks(data.tracks.items);
  };

  const onTrackClick = (trackId) => {
    console.log(trackId);
    getPlaylistTracks();
  };

  const renderTracks = () => {
    const max = tracks.length;
    const min = 2;
    const a = Math.floor(Math.random() * (max - min + 1)) + min;
    return tracks.slice(a - 2, a).map((track) => (
      <div key={track.track.id}>
        {track.track.album.images.length ? (
          <img
            width={"150px"}
            src={track.track.album.images[0].url}
            alt=""
            onClick={onTrackClick(track.track.id)}
          />
        ) : (
          <div>No Image</div>
        )}
        <p>{track.track.name}</p>
      </div>
    ));
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Versus My Playlists</h1>
        {!token ? (
          <a
            href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`}
            className="spotify-login"
          >
            Login with Spotify
          </a>
        ) : (
          <button className="spotify-logout" onClick={logout}>
            Logout
          </button>
        )}
        {renderTracks()}
      </header>
    </div>
  );
}

export default App;
